#include<stdio.h>
// Submitted version

int main()
{
    int testCaseCount;
    scanf("%d",&testCaseCount); 
    
    for(int count=0; count<testCaseCount; count++)
    {
    // Step 1 ----> Get the Input    
    
    int row = 0;
    scanf("%d",&row);
    int column = 0;
    scanf("%d",&column);
    int matrix[row][column];
    int size = (((row*column) * 2));

    char string[size];
    char newLineFeed = getchar();
    scanf("%[^\n]s",string);

    int rowCount = 0;
    int columnCount = 0;

    for(int i=0; string[i] != '\0'; i++)
    {
        if(string[i] != 32) {
            if(rowCount < row && columnCount < column) {
                matrix[rowCount][columnCount] = string[i] - '0';
                columnCount++;
            } else {
                rowCount++;
                columnCount = 0;
                matrix[rowCount][columnCount] = string[i] - '0';
                columnCount++;
            }
        }
    }

  // Step 2 ----> check if the array has atleast one(count) of '1'(vlaue)

    int checkBit = 0;

    for(int i=0; i<row; i++)
    {
        for(int j=0; j<column; j++)
        {
            if(matrix[i][j] == 1) {
                checkBit = 1;
                i = row;
                j = column;
            } else {
                checkBit = 0;
            }
        }
    }
    
  // Step 3 ----> Depend on the checkbit the program flow Changes

    int index = 0;
    int counter = 0;
    int frequency[row];
    int zeroCount = 0;
    
    if(checkBit) {
        for(int i=0; i<row; i++)  // count the frequency
        {
            for(int j=0; j<column;)
            {
                if(matrix[i][j] == 1) {
                    ++counter;
                    frequency[i] = counter;
                    j++;
                } else {
                    frequency[i] = counter;
                    j++;
                }
            }
            counter = 0;
        }

        for(int i=0; i<row; i++) // Count how many zeros are in the array
        {
            if(frequency[i] == 0) {
                zeroCount++;
            }
        }

        int min[row-zeroCount];
        int rowMin = -1;
        for(int i=0; i<row; i++)  // Insert non-zero values into the min[] array
        {
            if(frequency[i] != 0) {
                rowMin++;
                min[rowMin] = frequency[i]; 
            }
        }

        for(int i=0; i<(row-zeroCount); i++) // Arrange min[] in ascending order
        {
            for(int j=i+1; j<(row-zeroCount);)
            {
                if(min[j] < min[i]) {
                    int temp = min[j];
                    min[j] = min[i];
                    min[i] = temp;
                    j++;
                } else {
                    j++;
                }
            }
        }

        for(int i=0; i<row;)  // Find the index 
        {
            if(min[0] == frequency[i]) {
                index = i;
                i = row;
            } else {
                i++;
            }
        }
        
    } else {
        index = -1;
    }

  // Step 4 ----> Print the Result

    printf("%d\n",index);
    
    // Step 5 ----> Flush the input and frequency

        for(int i=0; i<row; i++)
        {
            for(int j=0; j<column; j++)
            {
                matrix[i][j] = 0;
            }
            frequency[i] = 0;
        }
    }
    
	return 0;
}


