            {
                if(negativeGreatest == sumHolder[i]) {
                    negativeGreatest = sumHolder[i];
                    value1 = variableOneHolder[i];
                    value2 = variableTwoHolder[i];
                    finalPosition = i;
                    repeatedCount++;
                }
            }

